SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_dados_amostra (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_grupo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_tempo_w	real;
qt_volume_w	integer;
ds_retorno_w	varchar(255);
ie_padrao_amostra_w	varchar(5);

/*
1 - Volume
2 - Tempo
*/
BEGIN


select	max(a.qt_tempo),
	max(a.qt_volume)
into STRICT	qt_tempo_w,
	qt_volume_w
from 	prescr_proc_material a,
	material_exame_lab b
where 	a.nr_prescricao 				= nr_prescricao_p
and	a.nr_Seq_material				= b.nr_sequencia
and	ie_volume_tempo				=	'S'
and	a.nr_seq_material				= nr_seq_material_p
and	((CASE WHEN a.nr_seq_grupo=0 THEN nr_seq_grupo_p  ELSE a.nr_seq_grupo END  	= nr_seq_grupo_p) or (coalesce(a.nr_seq_grupo::text, '') = ''));

if (coalesce(qt_tempo_w::text, '') = '') and (coalesce(qt_volume_w::text, '') = '') then

	select 	MAX(a.ie_padrao_amostra)
	into STRICT 	ie_padrao_amostra_w
	from 	lab_parametro a,
		prescr_medica b
	where 	a.cd_estabelecimento = b.cd_estabelecimento
	and	b.nr_prescricao = nr_prescricao_p;

	if (ie_padrao_amostra_w in ('PM','PM11','PMR11','PM13')) then
		select	max(a.qt_tempo),
			max(a.qt_volume)
		into STRICT	qt_tempo_w,
			qt_volume_w
		from 	prescr_proc_material a,
			material_exame_lab b
		where 	a.nr_prescricao 			= nr_prescricao_p
		and	a.nr_Seq_material			= b.nr_sequencia
		and	ie_volume_tempo				=	'S'
		and	a.nr_seq_material			= nr_seq_material_p;
	end if;
end if;

if (ie_opcao_p = 1) then
	ds_retorno_w := qt_volume_w;
else
	ds_retorno_w := qt_tempo_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
