CREATE OR REPLACE FUNCTION lab_obter_dados_estagio_autor ( NR_PRESCRICAO_P text , NR_SEQ_PRESCR_P text , IE_INFO_RETORNO_P text ) RETURNS varchar AS $body$
DECLARE


  nr_seq_estagio_w  AUTORIZACAO_CONVENIO.nr_seq_estagio%type;
  DS_ESTAGIO_w   ESTAGIO_AUTORIZACAO.DS_ESTAGIO%type;

BEGIN

        select a.nr_seq_estagio,
	       d.DS_ESTAGIO
	into STRICT   nr_seq_estagio_w,
		   DS_ESTAGIO_w
	from   AUTORIZACAO_CONVENIO a,
		   PROCEDIMENTO_AUTORIZADO b,
		   prescr_procedimento c,
		   ESTAGIO_AUTORIZACAO d
	where b.NR_SEQUENCIA_AUTOR = a.nr_sequencia
	and  b.nr_prescricao = c.nr_prescricao
	and   c.nr_sequencia = b.NR_SEQ_PRESCRICAO
	and  a.nr_seq_estagio = d.nr_sequencia
	and  c.nr_prescricao = nr_prescricao_p
	and  c.nr_sequencia = nr_seq_prescr_p;

       IF ( ie_info_retorno_p = 'SEQ' ) THEN
          RETURN nr_seq_estagio_w;
       ELSIF (ie_info_retorno_p = 'DS') THEN
          RETURN  DS_ESTAGIO_w;
       END IF;
 EXCEPTION
    WHEN others  THEN
   return '';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
