CREATE OR REPLACE FUNCTION lab_obter_dados_exame_desap (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nm_usuario_aprov_w	varchar(255);
dt_aprovacao_w		varchar(255);


BEGIN

select       MAX(substr(Obter_Nome_Usuario(a.nm_usuario_aprovacao),1,255)),
                MAX(to_char(a.dt_aprovacao,'dd/mm/yyyy hh24:mi:ss'))
into STRICT	nm_usuario_aprov_w,
	dt_aprovacao_w
from 	w_exame_lab_result_item a,
	exame_lab_resultado b
where	a.nr_seq_resultado = b.nr_seq_resultado
and	b.nr_prescricao = nr_prescricao_p
and	a.nr_seq_prescr = nr_seq_prescr_p;

if (ie_opcao_p = 'DT') then
	ds_retorno_w := dt_aprovacao_w;
elsif (ie_opcao_p = 'NM') then
	ds_retorno_w := nm_usuario_aprov_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
