CREATE OR REPLACE FUNCTION lab_obter_dados_integracao (nr_seq_empresa_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


--CD - Identificador da empresa
--ES - Estabelecimento
ds_retorno_w		 varchar(10);
nr_identificador_w	 varchar(10);
cd_estabelecimento_w varchar(5);


BEGIN
	if (nr_seq_empresa_p IS NOT NULL AND nr_seq_empresa_p::text <> '') then
		select 	max(nr_identificador),
				MAX(cd_estabelecimento)
		into STRICT 	nr_identificador_w,
				cd_estabelecimento_w
		from 	empresa_integr_dados
		where 	nr_seq_empresa_integr = nr_seq_empresa_p;

		if (ie_tipo_p = 'CD') then
			ds_retorno_w := nr_identificador_w;
		elsif (ie_tipo_p = 'ES') then
			ds_retorno_w := cd_estabelecimento_w;
		end if;

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
