CREATE OR REPLACE FUNCTION lab_obter_dados_lib_laudar ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
nr_sequencia_w		bigint;

dt_atualizacao_nrec_w	varchar(30);
nm_usuario_nrec_w	varchar(100);

/*
U	- Usu√°rio
D	- Data
*/
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	prescr_proc_lib_caso a
	where	a.nr_prescricao = nr_prescricao_p
	and	a.nr_seq_prescr = nr_seq_prescricao_p;

	select	max(to_char(a.dt_atualizacao_nrec,'dd/mm/yyyy hh24:mi:ss')),
		max(a.nm_usuario_nrec)
	into STRICT	dt_atualizacao_nrec_w,
		nm_usuario_nrec_w
	from	prescr_proc_lib_caso a
	where	a.nr_sequencia = nr_sequencia_w;

	if (ie_opcao_p = 'U') then
		ds_retorno_w	:= nm_usuario_nrec_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= dt_atualizacao_nrec_w;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
