CREATE OR REPLACE FUNCTION lab_obter_dados_pac_med_uso ( cd_pessoa_fisica_p paciente_medic_uso.cd_pessoa_fisica%TYPE, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w              varchar(2000);
    ds_lista_medicamentos_w   varchar(2000);
    ds_medicamento_w          varchar(255);
    ds_lista_dose_w           varchar(2000);
    qt_dose_w                 paciente_medic_uso.qt_dose%TYPE;
    ds_lista_ultima_dose_w    varchar(2000);
    dt_ultima_dose_w          paciente_medic_uso.dt_ultima_dose%TYPE;
    c01 CURSOR FOR
    SELECT
        coalesce(b.ds_material, a.ds_medicamento),
        a.qt_dose,
        a.dt_ultima_dose
    FROM paciente_medic_uso a
LEFT OUTER JOIN material b ON (a.cd_material = b.cd_material)
WHERE coalesce(a.dt_inativacao::text, '') = '' AND a.cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN
    OPEN c01;
    LOOP
        FETCH c01 INTO
            ds_medicamento_w,
            qt_dose_w,
            dt_ultima_dose_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
        BEGIN
            IF (ds_lista_medicamentos_w IS NOT NULL AND ds_lista_medicamentos_w::text <> '') THEN
                ds_lista_medicamentos_w := substr(ds_lista_medicamentos_w || ' , ', 1, 2000);
            END IF;

            ds_lista_medicamentos_w := substr(ds_lista_medicamentos_w || ds_medicamento_w, 1, 2000);
            IF (ds_lista_dose_w IS NOT NULL AND ds_lista_dose_w::text <> '') THEN
                ds_lista_dose_w := substr(ds_lista_dose_w || ' , ', 1, 2000);
            END IF;

            ds_lista_dose_w := substr(ds_lista_dose_w || qt_dose_w, 1, 2000);
            IF (ds_lista_ultima_dose_w IS NOT NULL AND ds_lista_ultima_dose_w::text <> '') THEN
                ds_lista_ultima_dose_w := substr(ds_lista_ultima_dose_w || ' , ', 1, 2000);
            END IF;

            ds_lista_ultima_dose_w := substr(ds_lista_ultima_dose_w || dt_ultima_dose_w, 1, 2000);
        END;

    END LOOP;

    CLOSE c01;
    IF ( ie_opcao_p = 'M' ) THEN
        ds_retorno_w := ds_lista_medicamentos_w;
    ELSIF ( ie_opcao_p = 'Q' ) THEN
        ds_retorno_w := ds_lista_dose_w;
    ELSIF ( ie_opcao_p = 'D' ) THEN
        ds_retorno_w := ds_lista_ultima_dose_w;
    END IF;

    RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
