CREATE OR REPLACE FUNCTION lab_obter_dados_pac_prescricao ( nr_prescricao_p bigint, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w bigint;
ie_envia_telef_cel_ws_w varchar(1);
retorno_w varchar(100);


BEGIN
retorno_w := null;
cd_estabelecimento_w :=0;

begin
    select cd_estabelecimento
    into STRICT cd_estabelecimento_w
    from prescr_medica a
    where a.nr_prescricao = nr_prescricao_p;
exception when no_data_found then
    cd_estabelecimento_w := 0;
end;

begin
    select ie_envia_telef_cel_ws
    into STRICT ie_envia_telef_cel_ws_w
    from lab_parametro a
    where a.cd_estabelecimento = cd_estabelecimento_w;
exception when no_data_found then
    ie_envia_telef_cel_ws_w := null;
end;

if (cd_estabelecimento_w > 0) then
    if (ie_envia_telef_cel_ws_w = 'S') then
        retorno_w := OBTER_DADOS_PF(cd_pessoa_fisica_p, 'TC');
    else
        retorno_w := OBTER_COMPL_PF(cd_pessoa_fisica_p, 1, 'T');
    end if;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
