CREATE OR REPLACE FUNCTION lab_obter_dados_regra_integr ( nm_usuario_p text, ie_opcao_p text, nm_instituto_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(30) := null;


BEGIN

-- ie_opcao_p
-- U = Usuário integração
if (ie_opcao_p = 'U') then

	select  	max(nm_usuario_integr)
	into STRICT	ds_retorno_w
	from 	lab_regra_usuario_integr
	where 	nm_usuario_tasy = nm_usuario_p
	and	upper(nm_instituto) = upper(nm_instituto_p);

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  	max(nm_usuario_integr)
		into STRICT	ds_retorno_w
		from 	lab_regra_usuario_integr
		where 	upper(nm_instituto) = upper(nm_instituto_p);

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
