CREATE OR REPLACE FUNCTION lab_obter_dado_grafico (nr_seq_exame_p exame_grafico.nr_seq_exame%type, cd_tipo_grafico_exame_p exame_grafico.cd_tipo_grafico_exame%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);


BEGIN

if (ie_opcao_p = 'NS') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	exame_grafico
	where	nr_seq_exame = nr_seq_exame_p
	and	cd_tipo_grafico_exame = cd_tipo_grafico_exame_p;
elsif (ie_opcao_p = 'QR') then
	select	max(qt_res_anteriores)
	into STRICT	ds_retorno_w
	from	exame_grafico
	where	nr_seq_exame = nr_seq_exame_p
	and	cd_tipo_grafico_exame = cd_tipo_grafico_exame_p;
end if;

return	ds_retorno_w;

end;
