CREATE OR REPLACE FUNCTION lab_obter_desc_tempo_amostra (nr_seq_int_prescr_p bigint, nr_seq_material_p bigint, nr_amostra_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_exame_w		bigint;
ds_tempo_amostra_w	varchar(20);
ds_material_w		varchar(255);


BEGIN

ds_retorno_w := substr(trim(both obter_descricao_padrao('MATERIAL_EXAME_LAB','DS_MATERIAL_EXAME',nr_seq_material_p)) || ' ' || nr_amostra_p || 'Âª' || ' '||lower(obter_desc_expressao(283435)),0,65);

select	max(c.nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_proc_material a,
	prescr_proc_mat_item b,
	prescr_procedimento c
where	a.nr_sequencia = b.nr_seq_prescr_proc_mat
and	b.nr_seq_prescr = c.nr_sequencia
and	b.nr_prescricao = c.nr_prescricao
and	a.nr_amostra = nr_amostra_p
and	a.nr_seq_material = nr_seq_material_p
and	a.nr_seq_int_prescr = nr_seq_int_prescr_p
and	a.nr_prescricao = nr_prescricao_p;

if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
	select	max(ds_tempo_amostra)
	into STRICT	ds_tempo_amostra_w
	from	exame_laboratorio
	where	nr_seq_superior = nr_seq_exame_w
	and	nr_ordem_amostra = nr_amostra_p;

	if (ds_tempo_amostra_w IS NOT NULL AND ds_tempo_amostra_w::text <> '') then
		ds_retorno_w := ds_retorno_w || ' (' || ds_tempo_amostra_w || ')';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
