CREATE OR REPLACE FUNCTION lab_obter_dias_entrega_exame (nr_seq_grupo_p bigint, nr_seq_exame_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

qt_dias_entrega_w	bigint;
nr_seq_grupo_w		bigint;
ds_retorno_w		varchar(50);

/* ie_opcao_p
	1 - qt_dias_entrega
*/
C01 CURSOR FOR
	SELECT	coalesce(qt_dia_entrega,0)
	from 	exame_lab_regra_setor
	where 	coalesce(nr_seq_grupo, nr_seq_grupo_w) = nr_seq_grupo_w
	and 	coalesce(nr_seq_exame, nr_seq_exame_p) = nr_seq_exame_p
	order by coalesce(nr_seq_prioridade,0), coalesce(nr_seq_exame,0), coalesce(nr_seq_grupo,0);


BEGIN

nr_seq_grupo_w := coalesce(nr_seq_grupo_p,0);

open C01;
loop
	fetch C01 into	qt_dias_entrega_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	if (ie_opcao_p=1) then
		ds_retorno_w	:= qt_dias_entrega_w;
	end if;
end loop;
close C01;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
