CREATE OR REPLACE FUNCTION lab_obter_exames_concat ( nr_prescricao_p bigint, dt_resultado_p timestamp, ie_urgente_p text, ie_opcao_p text, cd_setor_atendimento_p text, cd_setor_entrega_p text, ie_coleta_p text, dt_coleta_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);

C01 CURSOR FOR
	SELECT	distinct
		c.nm_exame,
		c.cd_exame
	FROM 	prescr_medica a,
		prescr_procedimento b,
		exame_laboratorio c
	WHERE	a.nr_prescricao = b.nr_prescricao
	AND	b.nr_seq_exame = c.nr_seq_exame
	AND 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	AND	(b.dt_resultado IS NOT NULL AND b.dt_resultado::text <> '')
	and 	coalesce(b.ie_suspenso,'N') <> 'S'
	AND  	b.ie_status_atend BETWEEN 10 AND 35
	and	a.nr_prescricao = nr_prescricao_p
	and	b.dt_resultado = dt_resultado_p
	and	b.ie_urgencia = ie_urgente_p
	and	b.cd_setor_atendimento = cd_setor_atendimento_p
	and	coalesce(b.cd_setor_entrega,0) = coalesce(cd_setor_entrega_p,0)
	and	((ie_coleta_p = 'N') or ((ie_coleta_p = 'S') and (coalesce(b.dt_coleta,'01/01/1900') = coalesce(dt_coleta_p,'01/01/1900'))))
	ORDER BY c.nm_exame;

c01_w	C01%rowtype;

BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'N') then
		ds_retorno_w := ds_retorno_w||';'||c01_w.nm_exame;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := ds_retorno_w||';'||c01_w.cd_exame;
	end if;
	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then

	ds_retorno_w := substr(ds_retorno_w,2,length(ds_retorno_w));

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
