CREATE OR REPLACE FUNCTION lab_obter_exames_lote_externo ( nr_prescricao_p bigint, nr_lote_p bigint, ie_opcao_p text, ie_separador_p text) RETURNS varchar AS $body$
DECLARE



nr_seq_exame_w				bigint;
cd_exame_w				varchar(20);
nm_exame_w				varchar(80);
Resultado_w	 			varchar(2000);

c01 CURSOR FOR
	SELECT	distinct
		b.nr_seq_exame,
		b.nm_exame,
		b.cd_exame
	from	lab_lote_externo c,
		exame_laboratorio b,
		prescr_procedimento a
	where a.nr_seq_exame		= b.nr_seq_exame
	  and a.nr_seq_lote_externo	= c.nr_sequencia
	  and a.nr_prescricao		= coalesce(nr_prescricao_p, a.nr_prescricao)
	  and c.nr_sequencia		= nr_lote_p;


BEGIN

/* Opções
	EX	- Exame
	CE	- Código Exame
	NM	- Nome Exame
*/
OPEN C01;
LOOP
FETCH C01 into	nr_seq_exame_w,
		nm_exame_w,
		cd_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'EX') then
		Resultado_w	:= Resultado_w || nr_seq_exame_w || ie_separador_p;
	elsif (ie_opcao_p = 'CE') then
		Resultado_w	:= Resultado_w || cd_exame_w || ie_separador_p;
	elsif (ie_opcao_p = 'NM') then
		Resultado_w	:= Resultado_w || nm_exame_w || ie_separador_p;

	end if;
	end;
END LOOP;

RETURN resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
