SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_exames_recoletados (nr_prescricao_p text, ds_sequencias_p text, ds_separador_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);

c01 CURSOR FOR
	SELECT	substr(a.nm_exame,1,50) nm_exame
	from	exame_laboratorio a,
		prescr_procedimento b,
		prescr_proc_recoleta c
	where	a.nr_seq_exame = b.nr_seq_exame
	and	b.nr_prescricao = c.nr_prescricao
	and	b.nr_sequencia  = c.nr_seq_prescr
	and	b.nr_prescricao = nr_prescricao_p
	and	obter_se_contido_char(b.nr_sequencia,ds_sequencias_p) = 'S'
	order by 1;

c01_w	c01%rowtype;



BEGIN

ds_retorno_w := null;

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := c01_w.nm_exame;
	else
		ds_retorno_w := ds_retorno_w ||ds_separador_p||c01_w.nm_exame;
	end if;
	end;
end loop;
close c01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
