CREATE OR REPLACE FUNCTION lab_obter_exame_concat_volume ( nr_prescricao_p text, nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


ds_exame_concat_vol_w 	varchar(50);


BEGIN

SELECT  DISTINCT coalesce(d.cd_exame_integracao,d.cd_exame)||' '||b.qt_volume
into STRICT	ds_exame_concat_vol_w
FROM   	prescr_medica a,
 	prescr_proc_material b,
	PRESCR_PROC_MAT_ITEM e,
	prescr_procedimento c,
	exame_laboratorio d
WHERE   a.nr_prescricao = b.nr_prescricao
AND	a.nr_prescricao = c.nr_prescricao
AND	c.nr_seq_exame = d.nr_seq_exame
AND	c.nr_prescricao = e.nr_prescricao
AND	c.nr_sequencia  = e.nr_seq_prescr
AND	e.NR_SEQ_PRESCR_PROC_MAT = b.nr_sequencia
AND	a.nr_prescricao = nr_prescricao_p
AND	c.nr_sequencia = nr_sequencia_p
AND	b.qt_volume > 0;

return	ds_exame_concat_vol_w;


end;
