SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_formato_item ( nr_seq_formato_p bigint, nr_linha_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
C01 CURSOR FOR 
	SELECT	distinct 
		substr(b.nm_exame,1,40), 
		j.nr_linha 
	from	exame_lab_format_item j, 
		exame_laboratorio b 
	where	j.nr_seq_exame	= b.nr_seq_exame 
	and	j.nr_seq_formato	= nr_seq_formato_p 
	order by 2,1;

 
ds_retorno_w			varchar(254);
ds_exame_w			varchar(200);
nr_linha_w			smallint;
qt_registros_w		smallint := 0;

 

BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	ds_exame_w, 
	nr_linha_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	begin 
			 
	qt_registros_w := qt_registros_w + 1;
	 
	if (qt_registros_w = nr_linha_p) then 
		ds_retorno_w := ds_exame_w;
		exit;
	end if;
	end;
end loop;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
