CREATE OR REPLACE FUNCTION lab_obter_frasco_exame (nr_seq_exame_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_frasco_w		varchar(255);


BEGIN

SELECT	max(b.ds_frasco)
into STRICT	ds_frasco_w
FROM	exame_lab_frasco a,
		frasco_exame_lab b
WHERE	a.nr_seq_frasco	  = b.nr_sequencia
AND		a.nr_seq_material 	  = nr_seq_material_p
AND		a.nr_seq_exame 	  = nr_seq_exame_p
and		coalesce(a.ie_situacao,'A') = 'A';

return	ds_frasco_w;

end;
