CREATE OR REPLACE FUNCTION lab_obter_idade_pf ( cd_pessoa_fisica_p text, dt_atual_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_nascimento_w	pessoa_fisica.dt_nascimento%type;
dt_obito_w	pessoa_fisica.dt_obito%type;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(dt_nascimento),
		max(dt_obito)
	into STRICT	dt_nascimento_w,
		dt_obito_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_opcao_p = 'DIA') then
		return trunc((obter_idade(dt_nascimento_w, coalesce(dt_obito_w, dt_atual_p), ie_opcao_p))::numeric );
	else
		return obter_idade(dt_nascimento_w, coalesce(dt_obito_w, dt_atual_p), ie_opcao_p);
	end if;
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
