CREATE OR REPLACE FUNCTION lab_obter_inf_horario_prescr (nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_origem_p bigint, dt_prevista_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_total_prescr_w	smallint;
qt_prescr_w		smallint;
ds_retorno_w		varchar(20);
nr_seq_origem_w		integer;

BEGIN

select	coalesce(nr_seq_origem,nr_sequencia)
into STRICT	nr_seq_origem_w
from 	prescr_procedimento
where 	nr_sequencia = nr_seq_prescr_p
and	nr_prescricao = nr_prescricao_p;

select 	count(*) + 1
into STRICT	qt_total_prescr_w
from 	prescr_procedimento
where	nr_seq_origem = coalesce(nr_seq_origem_p,nr_seq_prescr_p)
and	nr_prescricao = nr_prescricao_p;

if (coalesce(nr_seq_origem_p::text, '') = '') then
	qt_prescr_w := 1;
else
	select CASE WHEN count(*)=0 THEN 1  ELSE count(*) END
	into STRICT	qt_prescr_w
	from 	prescr_proc_hor
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_procedimento = nr_seq_origem_w
	and	dt_horario <= dt_prevista_p
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
end if;

ds_retorno_w	:= qt_prescr_w || '/' || qt_total_prescr_w;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
