SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_instituicao_amostra (nr_seq_instituicao_p bigint) RETURNS varchar AS $body$
DECLARE

				 
cd_cgc_w				varchar(80);
cd_pessoa_fisica_w		varchar(80);
ie_tipo_vinculo_w		varchar(1);
ds_razao_social_inst_w	varchar(80);
ds_instituicao_w		varchar(255);
ds_retorno_w			varchar(255)	:= '';


BEGIN 
 
if (nr_seq_instituicao_p IS NOT NULL AND nr_seq_instituicao_p::text <> '') then 
	 
	select 	max(cd_cgc), 
			max(substr(ds_razao_social_inst,1,80)), 
			max(cd_pessoa_fisica), 
			max(substr(ds_instituicao,1,255)), 
			max(ie_tipo_vinculo) 
	into STRICT	cd_cgc_w, 
			ds_razao_social_inst_w, 
			cd_pessoa_fisica_w, 
			ds_instituicao_w, 
			ie_tipo_vinculo_w 
	from	lote_ent_instituicao 
	where	nr_sequencia = nr_seq_instituicao_p;
	 
	if (ie_tipo_vinculo_w = 'I') then 
		ds_retorno_w	:= substr(coalesce(ds_razao_social_inst_w, obter_razao_social(cd_cgc_w)),1,80);
	elsif (ie_tipo_vinculo_w = 'P') then 
		ds_retorno_w	:= substr(coalesce(ds_instituicao_w, obter_nome_pf(cd_pessoa_fisica_w)),1,80);		
	end if;
	 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
