CREATE OR REPLACE FUNCTION lab_obter_instituicao_ficha (nr_seq_lote_sec_p bigint) RETURNS varchar AS $body$
DECLARE

ds_razao_social_w	varchar(80) := '';

BEGIN

if (nr_seq_lote_sec_p IS NOT NULL AND nr_seq_lote_sec_p::text <> '') then

	select	max(i.ds_razao_social_inst)
	into STRICT	ds_razao_social_w
	from	lote_ent_secretaria s,
			lote_ent_instituicao i
	where	s.nr_seq_instituicao = i.nr_sequencia
	and		s.nr_sequencia = nr_seq_lote_sec_p;

end if;

return	ds_razao_social_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
