CREATE OR REPLACE FUNCTION lab_obter_intervalo_seq_lab (nr_prescricao_p bigint, nr_Seq_lab_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_prescr_w	integer;
ds_inf_prescr_w	varchar(50);
ds_retorno_w	varchar(500);

BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_prescr_w
from 	prescr_Procedimento a
where 	nr_prescricao = nr_prescricao_p
and	nr_seq_lab    = nr_Seq_lab_p
and	((nr_seq_origem IS NOT NULL AND nr_seq_origem::text <> '') or (exists (SELECT 1 from prescr_procedimento b
						where a.nr_prescricao = b.nr_prescricao
						and   b.nr_seq_origem = a.nr_sequencia)));
ds_retorno_w	:= '';

if (nr_seq_prescr_w > 0) then

	select 	substr(lab_obter_inf_horario_prescr(b.nr_prescricao, b.nr_sequencia,b.nr_Seq_origem,b.dt_prev_execucao),1,50)
	into STRICT	ds_inf_prescr_w
	from 	prescr_procedimento b
	where 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia  = nr_seq_prescr_w;

	ds_retorno_w	:= ds_inf_prescr_w;

end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
