CREATE OR REPLACE FUNCTION lab_obter_mat_int_equip ( nr_seq_exame_p bigint, cd_material_exame_p text, ds_sigla_equip_p text) RETURNS varchar AS $body$
DECLARE


cd_material_w		lab_exame_equip.cd_material_integr%type;
nr_seq_material_w	material_exame_lab.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_material_w
from	material_exame_lab
where	cd_material_exame = cd_material_exame_p;

select 	max(a.cd_material_integr)
into STRICT	cd_material_w
from	lab_exame_equip a,
	equipamento_lab b
where	a.cd_equipamento = b.cd_equipamento
  and	a.nr_seq_exame 	= nr_seq_exame_p
  and	a.nr_seq_material = coalesce(nr_seq_material_w, a.nr_seq_material)
  and	b.ds_sigla = ds_sigla_equip_p;

return	cd_material_w;

end;
