CREATE OR REPLACE FUNCTION lab_obter_medico_resp_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

cd_medico_resp_w 	varchar(10);
nm_pessoa_fisica_w 	varchar(100);
ds_retorno_w		varchar(255);
/* ie_opcao_p 
  1 - cd_medico_resp 
  2 - nm_pessoa_fisica 
*/
BEGIN 
select 	max(a.cd_medico_resp) 
into STRICT 	cd_medico_resp_w 
from	exame_lab_result_item	a, 
	exame_lab_resultado	b 
where 	b.nr_seq_resultado	= a.nr_seq_resultado 
and	b.nr_prescricao		= nr_prescricao_p 
and	a.nr_seq_prescr		= nr_seq_prescr_p 
and	(a.nr_seq_material IS NOT NULL AND a.nr_seq_material::text <> '');
if (ie_opcao_p = 1) then 
	ds_retorno_w	:= cd_medico_resp_w;
elsif (ie_opcao_p = 2) then 
	select 	SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,100) 
	into STRICT	nm_pessoa_fisica_w 
	from 	pessoa_fisica 
	where	cd_pessoa_fisica = cd_medico_resp_w;
	ds_retorno_w := nm_pessoa_fisica_w;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
