CREATE OR REPLACE FUNCTION lab_obter_minutos_hora (ds_hora_p text) RETURNS bigint AS $body$
DECLARE


qt_minutos_w	bigint;
ds_hora_formatada_w varchar(50);


BEGIN

ds_hora_formatada_w := substr(ds_hora_p,1,length(ds_hora_p)-3);

SELECT tempo_em_minutos
into STRICT qt_minutos_w
FROM
(
  SELECT horas, minutos,
	( horas * 60 ) + minutos tempo_em_minutos
  FROM (
    SELECT (SUBSTR( tempo, 1, position(':' in tempo)-1 ) )::numeric  horas
         , (SUBSTR( tempo, position(':' in tempo)+1 ) )::numeric  minutos
    FROM (
      SELECT ds_hora_formatada_w tempo

    ) alias7
  ) alias8
) alias9;


return	qt_minutos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
