CREATE OR REPLACE FUNCTION lab_obter_motivo_atraso ( nr_prescricao_p prescr_procedimento.nr_prescricao%type, nr_seq_prescr_p prescr_procedimento.nr_sequencia%type, cd_estabeleciemento_p lab_motivo_atraso.cd_estabelecimento%type ) RETURNS varchar AS $body$
