CREATE OR REPLACE FUNCTION lab_obter_nr_lote_externo (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

  nr_seq_lote_externo_w bigint;


BEGIN


select nr_seq_lote_externo
  into STRICT nr_seq_lote_externo_w
  from prescr_procedimento
    where nr_prescricao = nr_prescricao_p
    and nr_sequencia = nr_sequencia_p;

if (coalesce(nr_seq_lote_externo_w::text, '') = '')then
  select max(nr_seq_lote_externo)
  into STRICT nr_seq_lote_externo_w
  from lab_lote_item
    where nr_prescricao = nr_prescricao_p
    and nr_seq_prescr = nr_sequencia_p;
end if;

return	nr_seq_lote_externo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
