CREATE OR REPLACE FUNCTION lab_obter_nr_seq_prescr_interf ( nr_prescricao_p bigint, nr_seq_exame_p bigint, nr_seq_material_p bigint, ie_status_pos_envio_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_prescr_w			prescr_procedimento.nr_sequencia%type;
ie_status_pos_envio_w	lab_parametro.ie_status_pos_envio%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	a.ie_status_pos_envio
	into STRICT	ie_status_pos_envio_w
	from 	lab_parametro a,
			prescr_medica b
	where	a.cd_estabelecimento = b.cd_estabelecimento
		and	b.nr_prescricao = nr_prescricao_p;


	select  max(a.nr_sequencia)
	into STRICT	nr_seq_prescr_w
	from 	prescr_procedimento a,
			material_exame_lab b
	where 	a.nr_prescricao 	= nr_prescricao_p
	and  	a.ie_suspenso	 	<>   'S'
	and		a.ie_status_atend 	< 30
	and		a.cd_material_exame = b.cd_material_exame
	and  	a.ie_status_atend 	>=   ie_status_pos_envio_w
	and  	lab_obter_se_result_dig(a.nr_prescricao, a.nr_sequencia) =   'N'
	and  	a.nr_seq_exame 		= nr_seq_exame_p
	and  	b.nr_sequencia 		= nr_seq_material_p;

	if (coalesce(nr_seq_prescr_w::text, '') = '' ) then
		 select max(a.nr_sequencia)
		 into STRICT	nr_seq_prescr_w
		 from 	prescr_procedimento a,
				material_exame_lab b
		 where 	a.nr_prescricao 	= nr_prescricao_p
		 and  	a.ie_suspenso		<>   'S'
		 and	a.ie_status_atend 	< 30
		 and	a.cd_material_exame = b.cd_material_exame
		 and  	a.ie_status_atend 	>=  ie_status_pos_envio_w
		 and  	a.nr_seq_exame 		= 	nr_seq_exame_p
		 and  	b.nr_sequencia 		= 	nr_seq_material_p;

	end if;

	if (coalesce(nr_seq_prescr_w::text, '') = '' ) then
		select 	MAX(nr_sequencia)
		into STRICT	nr_seq_prescr_w
		from 	prescr_procedimento
		where 	nr_prescricao 	= nr_prescricao_p
		and 	ie_suspenso 	<> 'S'
		and  	ie_status_atend >=   ie_status_pos_envio_w
		and  	lab_obter_se_result_dig(nr_prescricao, nr_sequencia) = 'N'
		and 	nr_seq_exame = nr_seq_exame_p;
	end if;

	if (coalesce(nr_seq_prescr_w::text, '') = '' ) then
		select  MAX(nr_sequencia)
		into STRICT	nr_seq_prescr_w
		from prescr_procedimento
		where nr_prescricao 	= nr_prescricao_p
		and ie_suspenso 		<> 'S'
		and  ie_status_atend 	>=   ie_status_pos_envio_w
		and nr_seq_exame 		= nr_seq_exame_p;
	end if;
end if;

return	nr_seq_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
