CREATE OR REPLACE FUNCTION lab_obter_obs_chec_pac_amostr ( nr_prescricao_p prescr_proc_checagem_pac.nr_prescricao%type, nr_seq_prescr_p prescr_proc_checagem_pac.nr_seq_prescr%type) RETURNS PRESCR_PROC_CHECAGEM_PAC.DS_OBSERVACAO%TYPE AS $body$
