SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_obs_prescr_exames (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

obs_prescricao_w	varchar(2000);
cd_exame_w		varchar(20);
obs_prescr_exame_w	varchar(255);

C01 CURSOR FOR
	SELECT	p.ds_observacao_coleta,
		e.cd_exame
	from	prescr_procedimento p,
		exame_laboratorio e
	where	p.nr_prescricao = nr_prescricao_p
	and	p.nr_seq_exame = e.nr_seq_exame
	and	(p.ds_observacao_coleta IS NOT NULL AND p.ds_observacao_coleta::text <> '');

BEGIN
obs_prescricao_w := '';

open C01;
loop
fetch C01 into
	obs_prescr_exame_w,
	cd_exame_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	obs_prescricao_w := substr(obs_prescricao_w || cd_exame_w || '-' || obs_prescr_exame_w ||';',1,2000);

	end;
end loop;
close C01;

return	obs_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
