SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_obs_recoleta (nr_seq_resultado_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_recoleta_w  varchar(255);
nr_prescricao_w bigint;


BEGIN

IF (nr_seq_resultado_p IS NOT NULL AND nr_seq_resultado_p::text <> '' AND nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') THEN

	SELECT  MAX(nr_prescricao)
	INTO STRICT nr_prescricao_w
	FROM    exame_lab_resultado
	WHERE   nr_seq_resultado = nr_seq_resultado_p;

	IF (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') THEN
		SELECT  MAX(ds_obs_recoleta)
		INTO STRICT ds_observacao_recoleta_w
		FROM  prescr_procedimento
		WHERE  nr_prescricao = nr_prescricao_w
		AND nr_sequencia  = nr_seq_prescr_p;
	END IF;
END IF;

RETURN ds_observacao_recoleta_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
