CREATE OR REPLACE FUNCTION lab_obter_orientacao ( nr_seq_exame_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_orientacao_w		varchar(4000);


BEGIN

if (ie_opcao_p = 'M') then
	select	max(ds_orientacao_medicamento)
	into STRICT	ds_orientacao_w
	from	exame_laboratorio
	where	nr_seq_exame	= nr_seq_exame_p;
elsif (ie_opcao_p = 'P') then
	select	max(ds_orientacao_usuario)
	into STRICT	ds_orientacao_w
	from	exame_laboratorio
	where	nr_seq_exame	= nr_seq_exame_p;
end if;

return	ds_orientacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
