CREATE OR REPLACE FUNCTION lab_obter_pacote_ant (nr_seq_instituicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30);


BEGIN

if (nr_seq_instituicao_p IS NOT NULL AND nr_seq_instituicao_p::text <> '') then

	SELECT	MAX(a.cd_pacote_ext)
	into STRICT	ds_retorno_w
	FROM	lote_ent_inst_geracao a,
			lote_ent_secretaria b
	WHERE	a.nr_seq_instituicao = b.nr_seq_instituicao
