CREATE OR REPLACE FUNCTION lab_obter_qt_dias_dif (qtde_horas_p bigint) RETURNS varchar AS $body$
DECLARE


hora_formatada_com_dias_w	varchar(100);
ds_horas_resto_w		varchar(60);
qtde_dias			double precision;
horas_sobrando			double precision;
qt_segundos_p			double precision;


BEGIN

if (qtde_horas_p IS NOT NULL AND qtde_horas_p::text <> '') then

	qtde_dias := (qtde_horas_p)/24;
	horas_sobrando := qtde_horas_p - ((trunc(qtde_dias,0))*24);
	qt_segundos_p := horas_sobrando*3600;
	ds_horas_resto_w := trim(both REPLACE(TO_CHAR(FLOOR(dividir(dividir(qt_segundos_p,60),60)),'999,999,900'),',','.'))
			|| ':' || SUBSTR(TO_CHAR(FLOOR(MOD(dividir(qt_segundos_p,60),60)),'00'),2,2)
			|| ':' || SUBSTR(TO_CHAR(MOD(qt_segundos_p,60),'00'),2,2);
	hora_formatada_com_dias_w := ((trunc(qtde_dias,0))||' dia(s) ') ||' '|| ds_horas_resto_w;

	RETURN	hora_formatada_com_dias_w;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
