CREATE OR REPLACE FUNCTION lab_obter_qt_exames_prescr (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE

qt_exames_w		bigint := 0;


BEGIN
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

		select count(*)
		into STRICT	qt_exames_w
		from   prescr_procedimento
		where  nr_prescricao = nr_prescricao_p
		and    (nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
		and    coalesce(nr_seq_superior::text, '') = '';

	end if;

return	qt_exames_w;

end;
