SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_regra_alerta (nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_min_espera_w		bigint := 0;
dt_inicial_w		timestamp;
dt_final_w		timestamp;
ie_status_final_w		smallint;
ie_status_atend_w		smallint;
qt_tempo_w		bigint;
ie_alerta_w		varchar(1) := 'N';
ds_cor_w			varchar(15) := '0';


BEGIN

/*ie_opcao_p
A - Verificar se deve alertar
C - Retorna cÃ³digo da cor da regra*/
if (ie_opcao_p = 'A') then

	select 	max(coalesce(ie_status_atend,0))
	into STRICT	ie_status_atend_w
	from 	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_sequencia_p;

	if (ie_status_atend_w > 0) then

		select 	max(coalesce(dt_atualizacao,null))
		into STRICT 	dt_inicial_w
		from 	prescr_proc_etapa
		where 	nr_prescricao = nr_prescricao_p
		and 	nr_seq_prescricao = nr_sequencia_p
		and 	ie_etapa = ie_status_atend_w;

		select	max(coalesce(ie_status_final,0))
		into STRICT	ie_status_final_w
		from 	regra_alerta_tempo_etapa
		where 	ie_status_inicial = ie_status_atend_w;

		if (ie_status_final_w > 0) then

			select 	max(dt_atualizacao)
			into STRICT 	dt_final_w
			from 	prescr_proc_etapa
			where 	nr_prescricao = nr_prescricao_p
			and 	nr_seq_prescricao = nr_sequencia_p
			and 	ie_etapa = ie_status_final_w;

			select	max(coalesce(qt_tempo,0))
			into STRICT	qt_tempo_w
			from 	regra_alerta_tempo_etapa
			where 	ie_status_inicial = ie_status_atend_w
			and	ie_status_final = ie_status_final_w;

			begin
			select	round((coalesce(dt_final_w, clock_timestamp()) -  dt_inicial_w) * 1440)
			into STRICT	qt_min_espera_w
			;
			exception
			when 	others then
				qt_min_espera_w	:= 0;
			end;

			if (qt_min_espera_w > 0) and (qt_tempo_w > 0) and (qt_min_espera_w > qt_tempo_w) then
				ie_alerta_w := 'S';
			else
				ie_alerta_w := 'N';
			end if;

		end if;
	end if;

	RETURN	ie_alerta_w;

elsif (ie_opcao_p = 'C') then

	select 	max(coalesce(ie_status_atend,0))
	into STRICT	ie_status_atend_w
	from 	prescr_procedimento
	where 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_sequencia_p;

	if (ie_status_atend_w > 0) then

		select	max(coalesce(ds_cor,0))
		into STRICT	ds_cor_w
		from 	regra_alerta_tempo_etapa
		where 	ie_status_inicial = ie_status_atend_w;

	end if;

	RETURN	ds_cor_w;

end if;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
