CREATE OR REPLACE FUNCTION lab_obter_regra_estab_ext (cd_estab_lote_p bigint, cd_estab_prescr_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_est_w		integer;
nr_seq_regra_item_w	bigint;
nr_seq_regra_w		bigint;
ie_liberado_estab_w	varchar(1);
qt_itens_regra_w	integer;
qt_regra_w		integer;

c01 CURSOR FOR
	SELECT 	distinct
		nr_seq_regra
	from	regra_exame_lab_estab_item
	where	cd_estabelecimento = cd_estab_prescr_p;



BEGIN

--ie_possui_regra_w 	:= 'N';
ie_liberado_estab_w	:= 'S';


select 	count(*)
into STRICT	qt_regra_w
from 	regra_exame_lab_estab;

--select 	nvl(decode(count(*),0,'N','S'),'N')
--into	ie_possui_regra_w
select	count(*)
into STRICT	qt_regra_est_w
from	regra_exame_lab_estab
where	cd_estabelecimento = cd_estab_lote_p;

if (qt_regra_w > 0) then

	if (qt_regra_est_w > 0) then

		ie_liberado_estab_w	:= 'N';

		select 	max(nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	regra_exame_lab_estab
		where	cd_estabelecimento = cd_estab_lote_p;

		select 	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'S')
		into STRICT	ie_liberado_estab_w
		from	regra_exame_lab_estab_item
		where	cd_estabelecimento = cd_estab_prescr_p
		and	nr_seq_regra =  nr_seq_regra_w;

	end if;

	if (qt_regra_est_w = 0) and (cd_estab_lote_p <> cd_estab_prescr_p) then

		select 	count(*)
		into STRICT	qt_regra_est_w
		from	regra_exame_lab_estab
		where	cd_estabelecimento = cd_estab_prescr_p;

		if (qt_regra_est_w  > 0) then

			ie_liberado_estab_w	:= 'N';

			select 	max(nr_sequencia)
			into STRICT	nr_seq_regra_w
			from	regra_exame_lab_estab
			where	cd_estabelecimento = cd_estab_prescr_p;

			select 	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'S')
			into STRICT	ie_liberado_estab_w
			from	regra_exame_lab_estab_item
			where	cd_estabelecimento = cd_estab_lote_p
			and	nr_seq_regra =  nr_seq_regra_w;

		end if;


	end if;


	if (qt_regra_est_w = 0) and (cd_estab_lote_p <> cd_estab_prescr_p) then

		ie_liberado_estab_w	:= 'N';

		open c01;
		loop
		fetch c01 into
			nr_seq_regra_item_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin


			if (ie_liberado_estab_w	= 'N') then

				select 	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'S')
				into STRICT	ie_liberado_estab_w
				from	regra_exame_lab_estab_item
				where	cd_estabelecimento = cd_estab_lote_p
				and	nr_seq_regra = nr_seq_regra_item_w;
			end if;
			end;
		end loop;
		close c01;

	end if;

end if;

return ie_liberado_estab_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
