CREATE OR REPLACE FUNCTION lab_obter_regra_estab ( cd_estab_p bigint, cd_estab_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


--ie_possui_regra_w	varchar2(1);
qt_regra_w		integer;
nr_seq_regra_w		bigint;
ie_liberado_estab_w	varchar(1);


BEGIN

ie_liberado_estab_w	:= 'S';

select 	count(*)
into STRICT	qt_regra_w
from	regra_exame_lab_estab
where	cd_estabelecimento = cd_estab_p;


if (qt_regra_w > 0) and (cd_estab_p IS NOT NULL AND cd_estab_p::text <> '') and (cd_estab_prescr_p IS NOT NULL AND cd_estab_prescr_p::text <> '') then

	ie_liberado_estab_w	:= 'N';

	select 	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	regra_exame_lab_estab
	where	cd_estabelecimento = cd_estab_p;

	select 	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'S')
	into STRICT	ie_liberado_estab_w
	from	regra_exame_lab_estab_item
	where	cd_estabelecimento = cd_estab_prescr_p
	and	nr_seq_regra =  nr_seq_regra_w;
end if;

return ie_liberado_estab_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
