CREATE OR REPLACE FUNCTION lab_obter_regra_fatur ( cd_estabelecimento_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ie_faturamento_w	varchar(1);
nr_seq_grupo_w		bigint;

c01 CURSOR FOR
	SELECT ie_faturamento
	from lab_regra_faturamento
	where coalesce(nr_seq_grupo,nr_seq_grupo_w)	= nr_seq_grupo_w
	  and coalesce(nr_seq_exame,nr_seq_exame_p)	= nr_seq_exame_p
	  and cd_estabelecimento		= cd_estabelecimento_p
	order by coalesce(nr_seq_exame,0), coalesce(nr_seq_grupo,0);


BEGIN

select	coalesce(max(nr_seq_grupo),0)
into STRICT	nr_seq_grupo_w
from	exame_laboratorio
where	nr_seq_exame = nr_seq_exame_p;

open c01;
loop
	fetch c01 into ie_faturamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ie_faturamento_w := ie_faturamento_w;
end loop;
close c01;

return coalesce(ie_faturamento_w,'I');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
