		ds_regra_w := substr(ds_regra_w, position(']' in ds_regra_w) + 1, 255);
	end if;
	if (position('|' in ds_regra_w) > 0) then
		ds_regra_w := substr(ds_regra_w, position('|' in ds_regra_w) + 1, 255);
		ds_regra_w := substr(ds_regra_w, position('|' in ds_regra_w) + 1, 255);
	end if;
