if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
	ds_resultado_final_w:= ds_resultado_w;
elsif (qt_resultado_w IS NOT NULL AND qt_resultado_w::text <> '') then
	ds_resultado_final_w:= qt_resultado_w;
elsif (pr_resultado_w IS NOT NULL AND pr_resultado_w::text <> '') then
