CREATE OR REPLACE FUNCTION lab_obter_result_bloq_status ( nr_prescricao_p bigint, ie_status_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_resultado_w	exame_lab_resultado.nr_seq_resultado%type;


BEGIN

select	count(a.nr_sequencia)
into STRICT	nr_seq_resultado_w
from	prescr_procedimento a,
	exame_lab_resultado b,
	exame_lab_result_item c
where	a.nr_prescricao = nr_prescricao_p
  and 	b.nr_prescricao = a.nr_prescricao
  and	c.nr_seq_resultado = b.nr_seq_resultado
  and 	c.nr_seq_prescr = a.nr_sequencia
  and	a.ie_status_atend = ie_status_p
  and	coalesce(a.ie_suspenso, 'N') = 'N'
  and	(c.nr_seq_material IS NOT NULL AND c.nr_seq_material::text <> '');

if (nr_seq_resultado_w > 0) then

	select	CASE WHEN count(a.nr_sequencia)=0 THEN  'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	prescr_procedimento a,
		exame_lab_resultado b,
		exame_lab_result_item c
	where	a.nr_prescricao = nr_prescricao_p
	  and 	b.nr_prescricao = a.nr_prescricao
	  and	c.nr_seq_resultado = b.nr_seq_resultado
	  and 	c.nr_seq_prescr = a.nr_sequencia
	  and	a.ie_status_atend = ie_status_p
	  and	coalesce(a.ie_suspenso, 'N') = 'N'
	  and	(c.nr_seq_material IS NOT NULL AND c.nr_seq_material::text <> '')
	  and 	coalesce(c.ie_restringe_resultado, 'N') = 'N';

else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
