SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_result_exame_massas ( nr_prescricao_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		exame_lab_result_item.ds_resultado%type;
ie_exame_massas_w	varchar(1);


BEGIN

ie_exame_massas_w := 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

	select  coalesce(max('S'),'N')
	into STRICT	ie_exame_massas_w
	from  	lote_ent_exame_massa
	where	nr_seq_exame = nr_seq_exame_p
	and		coalesce(ie_situacao,'A') = 'A';

	if (ie_exame_massas_w = 'S') then
		SELECT 	MAX(substr(m.ds_resultado,1,255)) ds_resultado
		into STRICT	ds_resultado_w
		FROM	lab_patologia_exame a,
				lab_patologia b,
				exame_lab_resultado g,
				prescr_procedimento p,
				exame_laboratorio w,
				lote_ent_sec_ficha x,
				lote_ent_sec_ficha_exam y,
				exame_laboratorio z,
				exame_lab_result_item m
		WHERE	a.nr_seq_patologia = b.nr_sequencia
		AND   	w.nr_seq_exame = p.nr_seq_exame
		AND   	p.nr_prescricao = g.nr_prescricao
		AND   	coalesce(p.ie_suspenso,'N') <> 'S'
		AND   	z.nr_seq_superior = p.nr_seq_exame
		AND   	z.nr_seq_exame = a.nr_seq_exame
		AND   	x.nr_sequencia = y.nr_seq_ficha
		AND   	y.nr_seq_exame = p.nr_seq_exame
		AND   	p.nr_seq_exame = y.nr_seq_exame
		AND   	p.nr_prescricao = x.nr_prescricao
		AND m.nr_seq_resultado = g.nr_seq_resultado
		AND m.nr_seq_prescr = p.nr_sequencia
		AND p.nr_prescricao = nr_prescricao_p
		AND p.nr_seq_exame = nr_seq_exame_p
		AND (m.nr_seq_material IS NOT NULL AND m.nr_seq_material::text <> '')
		AND   	p.ie_status_atend >= 30
		AND   	EXISTS (SELECT 	1
						FROM	LOTE_ENT_EXAME_MASSA l
						WHERE	l.nr_seq_exame = p.nr_seq_exame
						AND		l.ie_situacao = 'A') LIMIT 1;
	end if;

end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION lab_obter_result_exame_massas ( nr_prescricao_p bigint, nr_seq_exame_p bigint) FROM PUBLIC;

