SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_result_item ( nr_seq_resultado_p bigint, nr_seq_prescr_p bigint, nr_sequencia_p bigint, ie_opcao_p text, ie_separador_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_material_w	bigint;
nr_seq_metodo_w		bigint;
cd_medico_resp_w	varchar(10);
nr_sequencia_w		bigint;
ds_referencia_w		varchar(4000);
nr_seq_exame_w		bigint;
ds_observacao_w		varchar(4000);

ds_retorno_w		varchar(255);
/*ie_opcao_p
	0 - descrição material
	1 - descrição método
	2 - sequencia dentro do exame
	3 - bioquimico
	4 - referência
	5 - sequencia exame
	6 - OBS exame principal
*/
BEGIN

select	nr_seq_material,
	nr_seq_metodo,
	cd_medico_resp,
	nr_sequencia,
	ds_referencia,
	nr_seq_exame,
	ds_observacao
into STRICT	nr_seq_material_w,
	nr_seq_metodo_w,
	cd_medico_resp_w,
	nr_sequencia_w,
	ds_referencia_w,
	nr_seq_exame_w,
	ds_observacao_w
from	exame_lab_result_item
where	nr_seq_resultado	= nr_seq_resultado_p
  and	nr_seq_prescr		= nr_seq_prescr_p
  and	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

if (ie_opcao_p = 0) then
	select	ds_material_exame
	into STRICT	ds_retorno_w
	from material_exame_lab
	where nr_sequencia = nr_seq_material_w;
elsif (ie_opcao_p = 1) then
	select	ds_metodo
	into STRICT	ds_retorno_w
	from metodo_exame_lab
	where nr_sequencia = nr_seq_metodo_w;
elsif (ie_opcao_p = 2) then
	if (nr_sequencia_p = nr_sequencia_w) then
		ds_retorno_w := '1';
	else
		select	nr_sequencia_p - min(nr_sequencia) + 2
		into STRICT	ds_retorno_w
		from exame_lab_result_item
		where	nr_seq_resultado= nr_seq_resultado_p
		  and	nr_seq_prescr	= nr_seq_prescr_p
		  and	coalesce(nr_seq_material::text, '') = '';
	end if;
elsif (ie_opcao_p = 3) then
	select CASE WHEN ie_sexo='M' THEN  obter_desc_expressao(779585)||' '  ELSE obter_desc_expressao(779583)||' ' END  || nm_pessoa_fisica || ' - CRF/' || ds_codigo_prof
	into STRICT ds_retorno_w
	from pessoa_fisica
	where cd_pessoa_fisica = cd_medico_resp_w;

elsif (ie_opcao_p = 4) then
	ds_retorno_w	:= ds_referencia_w;
elsif (ie_opcao_p = 5) then
	ds_retorno_w	:= to_char(nr_seq_exame_w);
elsif (ie_opcao_p = 6) then
	ds_retorno_w	:= ds_observacao_w;

end if;

select	CASE WHEN coalesce(ds_retorno_w,'')='' THEN ''  ELSE ds_retorno_w || ie_separador_p END
into STRICT	ds_retorno_w
;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
