CREATE OR REPLACE FUNCTION lab_obter_result_sugerido (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_result_sug_w				varchar(255);
nr_seq_exame_prescr_w		exame_laboratorio.nr_seq_exame%type;
nr_seq_material_prescr_w	material_exame_lab.nr_sequencia%type;
qt_resultado_w				exame_lab_result_item.qt_resultado%type;
ds_resultado_w				exame_lab_result_item.ds_resultado%type;
pr_resultado_w				exame_lab_result_item.pr_resultado%type;
									

BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then 
  
  
	select	max(a.nr_seq_exame), 
			max(b.nr_sequencia) 
	into STRICT	nr_seq_exame_prescr_w, 
			nr_seq_material_prescr_w 
	from	prescr_procedimento a, 
			material_exame_lab 	b 
	where 	a.cd_material_exame = b.cd_material_exame 
	and		a.nr_prescricao = nr_prescricao_p 
	and		a.nr_sequencia = nr_seq_prescr_p;
  
	select 	SUBSTR(lab_cons_valor_result_lote(nr_seq_exame_prescr_w, nr_seq_material_prescr_w, coalesce(a.qt_resultado,a.pr_resultado), a.ds_resultado, nr_prescricao_p, nr_seq_prescr_p,8),1,255) 
	into STRICT	ds_result_sug_w	 
	from	exame_lab_result_item a, 
			exame_lab_resultado b 
  where 	a.nr_seq_resultado 	= b.nr_seq_resultado 
	and		b.nr_prescricao 	= nr_prescricao_p 
	and		a.nr_seq_prescr		= nr_seq_prescr_p 
	and		a.nr_seq_exame 		= nr_seq_exame_prescr_w;
  
end if;
 
 
 
return	ds_result_sug_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
