CREATE OR REPLACE FUNCTION lab_obter_result_valor_format (ds_resultado_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_valor_w			varchar(255);
ds_primeiro_w		varchar(1);


BEGIN

ds_retorno_w := ds_resultado_p;

if (ds_resultado_p IS NOT NULL AND ds_resultado_p::text <> '') then
	ds_valor_w := obter_se_somente_ponto_numero(replace(ds_resultado_p ,',','.'));
	if (ds_valor_w = 'A') then
		begin
		ds_retorno_w := (replace(ds_resultado_p, '.',''))::numeric;
		exception
		when others then
			ds_retorno_w := (replace(ds_resultado_p ,'.',','))::numeric;
			end;

		select 	substr(ds_retorno_w,1,1)
		into STRICT	ds_primeiro_w
		;

		if (ds_primeiro_w = ',') then
			ds_retorno_w := '0'||ds_retorno_w;
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
