CREATE OR REPLACE FUNCTION lab_obter_seq_exame_shift ( nr_prescricao_p bigint, cd_material_exame_p text) RETURNS bigint AS $body$
DECLARE


cd_material_exame_w	varchar(50);
nr_sequencia_w		smallint;

c01 CURSOR FOR
	SELECT	distinct coalesce(ds_material_especial,cd_material_exame)
	from	prescr_procedimento
	where nr_prescricao = nr_prescricao_p;


BEGIN

nr_sequencia_w := 0;

open c01;
loop
fetch c01 into cd_material_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	if (cd_material_exame_w = cd_material_exame_p) then
		exit;
	end if;

	nr_sequencia_w := nr_sequencia_w + 1;
end loop;
close c01;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
