SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_se_aprova ( nr_seq_resultado_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_w	bigint;
qt_resultado_w	double precision;
pr_resultado_w	double precision;
ds_resultado_w	varchar(2000);
ds_regra_w	varchar(255);
ds_regra_aux_w	varchar(255) := null;
vl_resultado_w	double precision  := 1;
ds_parametros_w	varchar(255);
c01 CURSOR FOR
	SELECT	a.nr_seq_exame,
		a.qt_resultado,
		a.pr_resultado,
		a.ds_resultado,
		b.ds_regra
	from	exame_laboratorio b,
		exame_lab_result_item a
	where	a.nr_seq_exame		= b.nr_seq_exame
	  and	a.nr_seq_resultado	= nr_seq_resultado_p
	  and	a.nr_seq_prescr		= nr_seq_prescr_p
	  and	position('['  b.ds_regra)	> 0;



BEGIN

open c01;
loop
	fetch c01 into	nr_seq_exame_w,
			qt_resultado_w,
			pr_resultado_w,
			ds_resultado_w,
			ds_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	if (position('[' in ds_regra_w) > 0) then
		ds_regra_aux_w := substr(ds_regra_w, position('[' in ds_regra_w) + 1, 255);
		ds_regra_aux_w := substr(ds_regra_aux_w, 0, position(']' in ds_regra_aux_w) - 1);
	end if;

	if (ds_regra_aux_w IS NOT NULL AND ds_regra_aux_w::text <> '') then

		ds_regra_aux_w	:= replace(ds_regra_aux_w,'P',':valor');
		ds_regra_aux_w	:= replace(ds_regra_aux_w,'V',':valor');
		ds_regra_aux_w	:= replace(ds_regra_aux_w,'D',':valor');


		if ( position(':valor' in ds_regra_aux_w) = 0 ) then
			ds_regra_aux_w := ' :valor ' || ds_regra_aux_w;
		end if;

		ds_parametros_w := 'valor=';

		if (coalesce(qt_resultado_w,0) <> 0) then
			ds_parametros_w := ds_parametros_w || qt_resultado_w;
		elsif (coalesce(pr_resultado_w,0) <> 0) then
			ds_parametros_w := ds_parametros_w || pr_resultado_w;
		elsif (coalesce(ds_resultado_w,0) <> '') then
			ds_parametros_w := ds_parametros_w || ds_resultado_w;
		end if;

		ds_parametros_w := ds_parametros_w ||';';
		ds_regra_aux_w	:= replace(ds_regra_aux_w,',','.');

		vl_resultado_w := obter_valor_dinamico_bv('select count(*) from dual where ' || ds_regra_aux_w, ds_parametros_w, vl_resultado_w);

		if (vl_resultado_w = 0) then
			exit;
		end if;
	end if;
end loop;
close c01;

if (vl_resultado_w = 0) then
	return 'N';
else
	return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
