SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_se_atend_fechado ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_resultado_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_procedimento_w		bigint;
cd_motivo_baixa_w		smallint;
ie_conta_paciente_w		varchar(1);
nr_prescricao_ww		bigint;
nr_atendimento_ww		bigint;
ds_retorno_w			varchar(1);
qt_atendimento_fechado_w	bigint;
	

BEGIN 
 
ds_retorno_w := 'N';
 
select 	count(*) 
into STRICT 	qt_procedimento_w 
from 	procedimento_paciente 
where 	nr_prescricao = nr_prescricao_p 
and 	nr_sequencia_prescricao = nr_seq_prescr_p;
 
select	coalesce(cd_motivo_baixa_exame,coalesce(cd_motivo_baixa,0)) 
into STRICT	cd_motivo_baixa_w 
from 	prescr_procedimento 
where 	nr_prescricao = nr_prescricao_p 
and 	nr_sequencia = nr_seq_prescr_p;
 
if (cd_motivo_baixa_w = 0) then 
	select	coalesce(max(cd_motivo_baixa),1) 
	into STRICT	cd_motivo_baixa_w 
	from	lab_parametro 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	if (qt_procedimento_w = 0) then 
		select	ie_conta_paciente 
		into STRICT	ie_conta_paciente_w 
		from 	tipo_baixa_prescricao 
		where 	cd_tipo_baixa = cd_motivo_baixa_w 
		and 	ie_prescricao_devolucao = 'P';
		 
		if (ie_conta_paciente_w = 'S') then 
		 
			select 	max(nr_prescricao) 
			into STRICT	nr_prescricao_ww 
			from 	exame_lab_resultado 
			where 	nr_seq_resultado = nr_seq_resultado_p;
 
			select	max(nr_atendimento) 
			into STRICT	nr_atendimento_ww 
			from	prescr_medica 
			where	nr_prescricao = nr_prescricao_ww;
			 
			select	count(*) 
			into STRICT	qt_atendimento_fechado_w 
			from	atendimento_paciente 
			where	nr_atendimento = nr_atendimento_ww 
			and	(dt_fim_conta IS NOT NULL AND dt_fim_conta::text <> '');
		 
			if (qt_atendimento_fechado_w > 0) then 
				ds_retorno_w := 'S';
			else	 
				ds_retorno_w := 'N';
			end if;
			 
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
