CREATE OR REPLACE FUNCTION lab_obter_se_conta_paga (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w		varchar(1);


BEGIN 
ds_retorno_w	:=	'S';
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select 	CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from 	conta_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and 	((cd_convenio_parametro = 3) or (ie_status_acerto = 2 and coalesce(nr_seq_protocolo,0) <> 0));
	 
end if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
